sp_widget.client_script:
api.controller = function ($scope) {
    var canvas = document.getElementById('simulationChart');
    if (!canvas) {
        console.error("Canvas-Element nicht gefunden!");
        return;
    }

    var histogramData = $scope.data.histogramData || [];
    var confidenceThroughput = $scope.data.confidenceThroughput || null;

    if (histogramData.length === 0 || confidenceThroughput === null) {
        console.error("Keine Histogrammdaten oder kein Konfidenzwert verfügbar.");
        return;
    }

    // Extrahiere die Daten für das Bar-Diagramm
    var labels = histogramData.map(function (item) {
        return item.throughput; // Numerische Werte für die X-Achse
    });

    var frequencies = histogramData.map(function (item) {
        return item.frequency; // Häufigkeiten für die Y-Achse
    });

    new Chart(canvas.getContext('2d'), {
        type: 'bar',
        data: {
            labels: labels,
            datasets: [{
                label: 'Simulated number of completed Stories in 14 days',
                data: frequencies,
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                borderColor: 'rgba(75, 192, 192, 1)',
                borderWidth: 1
            }]
        },
        options: {
            plugins: {
                annotation: {
                    annotations: {
                        confidenceLine: {
                            type: 'line',
                            xMin: confidenceThroughput,
                            xMax: confidenceThroughput,
                            borderColor: 'red',
                            borderWidth: 2,
                            label: {
                                content: `85% (${confidenceThroughput} Stories)`,
                                enabled: true,
                                position: 'end',
                                backgroundColor: 'rgba(255, 99, 132, 0.8)',
                                color: 'white',
                                font: {
                                    size: 12,
                                    weight: 'bold'
                                },
                                padding: 6
                            }
                        }
                    }
                }
            },
            scales: {
                x: {
                    type: 'linear', // Numerische X-Achse
                    title: {
                        display: true,
                        text: 'Number of completed Stories'
                    },
                    ticks: {
                        stepSize: 10 // Optional: Schrittweite der Labels
                    }
                },
                y: {
                    beginAtZero: true,
                    title: {
                        display: true,
                        text: 'Frequency of occurrence'
                    }
                }
            }
        }
    });
};

sp_widget.script:
(function () {
    data.histogramData = [];
    var storyDataUtil = new MCStoryUtil();
    var monteCarloSim = new MonteCarloSimUtil();

    // Hole den täglichen Durchsatz aus den historischen Daten
    var dailyThroughput = storyDataUtil.getDailyThroughput();

    if (dailyThroughput.length > 0) {
        var simulationParams = {
            days: 14,         // Zeitraum der Simulation (z. B. 14 Tage)
            iterations: 10000 // Anzahl der Simulationen
        };

        // Führe die Monte-Carlo-Simulation durch
        var simulationResults = monteCarloSim.simulateThroughput(
            simulationParams.days,
            simulationParams.iterations,
            dailyThroughput
        );

        // Histogramm erstellen
        var histogram = simulationResults.reduce(function (acc, throughput) {
            acc[throughput] = (acc[throughput] || 0) + 1;
            return acc;
        }, {});

        // Konvertiere das Histogramm für das Client Script
        data.histogramData = Object.keys(histogram).map(function (key) {
            return {
                throughput: parseInt(key, 10),
                frequency: histogram[key]
            };
        });

        // Sortiere die Histogrammdaten nach Throughput
        data.histogramData.sort(function (a, b) {
            return a.throughput - b.throughput;
        });

        // Berechne die Dauer für 85% Konfidenz
        var totalFrequency = simulationResults.length;
        var cumulativeFrequency = 0;
        data.confidenceThroughput = null;

        for (var i = 0; i < data.histogramData.length; i++) {
            cumulativeFrequency += data.histogramData[i].frequency;
            if (!data.confidenceThroughput && (cumulativeFrequency / totalFrequency) >= 0.85) {
                data.confidenceThroughput = data.histogramData[i].throughput;
                break;
            }
        }
    } else {
        gs.info("Keine historischen Daten für die Monte-Carlo-Simulation verfügbar.");
    }
})();
